import React from 'react';
import { Account, AccountContext } from "../cognito/Account";
import userServices from "../services/user.services";
import{ useEffect, useContext, useState } from "react";


const FilterModal = props => {
    const { getSession } = useContext(AccountContext);
    const [logged, setLogged] = useState(false);
    const [interests, setInterests] = useState([]);

    const getInfo = (session) =>{
        userServices.getUser(session.idToken.payload['cognito:username']).then( data => {
            setInterests(data.data.interests);
        })
  }

    const InterestsToQuery = () =>{
        let query= interests.toString().toLowerCase().replace(',', "OR")
    }


    var Category = [
        {
            value: 'business',
            name: 'Business'
        },
        {
            value: 'entertainment',
            name: 'Entertainment'
        },
        {
            value: 'general',
            name: 'General'
        },
        {
            value: 'health',
            name: 'Health'
        },
        {
            value: 'science',
            name: 'Science'
        },
        {
            value: 'sports',
            name: 'Sports'
        },
        {
            value: 'technology',
            name: 'Technology'
        }
    ]
    var Country = [
        {
            value: 'us',
            name: 'Usa'
        },
        {
            value: 'in',
            name: 'India'
        },
        {
            value: 'au',
            name: 'Australia'
        },
        {
            value: 'ca',
            name: 'Canada'
        },
        {
            value: 'ch',
            name: 'China'
        }
    ]

    useEffect(() => { 
        getSession().then((session) => {  
            setLogged(true); 
            getInfo(session);
        });
      }, []);
    return (
        <div className="filters">
            <span className="custom_dropdown">
                <select name="Category" id="Category"
                    value={props.selectedValueCategory}
                    onChange={props.handleChangeCategory}>
                    {
                        Category.map(o =>
                            (<option key={o.value} value={o.value}>{o.name}</option>
                            ))
                    }
                </select>
            </span>
            <span className="custom_dropdown">
                <select name="Country" id="Country"
                    value={props.selectedValueCountry}
                    onChange={props.handleChangeCountry}>
                    {
                        Country.map(o =>
                            (<option key={o.value} value={o.value}>{o.name}</option>
                            ))
                    }
                </select>
            </span>
        </div>
    )
}

export default FilterModal;
