import React, { Component, useEffect, useContext, useState } from "react";
import { Account, AccountContext } from "../cognito/Account";
import ChangePassword from "../cognito/ChangePassword";
import userServices from "../services/user.services";
import "../css/user.css"

const CUser = () => {
    const { getSession } = useContext(AccountContext);
    const [loggedIn, setLoggedIn] = useState(false);
    const [info, setInfo] = useState([]);
    const [display, setDisplay] = useState(false)

    const sdisplay = () => {
        setDisplay(true)
    }

    useEffect(() => {
        getSession().then((session) => {
            setInfo(session.idToken.payload);
            setLoggedIn(true);
        });
    }, []);

    return ( <
        div className = "user-container" >
        <
        div className = "user-box" > {
            loggedIn ?
            <
            div >
            <
            div className = "user-title" > User Info < /div> <
            div className = "user-info" >
            Email : { info.email } <
            button onClick = { sdisplay() } > Change Password < /button> <
            /div> {
                display &&
                    <
                    div >
                    <
                    h2 > Change Password < /h2> <
                    ChangePassword / >
                    <
                    /div>
            } <
            /div>: < div > NO < /div>
        } <
        /div> <
        /div>
    );
};

export default class User extends Component {
    render() {
        return ( <
            div >
            <
            Account >
            <
            CUser / >
            <
            /Account> <
            /div>
        )
    };

};
