import React from 'react';
import { Account, AccountContext } from "../cognito/Account";
import userServices from "../services/user.services";
import{ Component, useEffect, useContext, useState } from "react";


const CFilterModal = props => {
    const { getSession } = useContext(AccountContext);
    const [logged, setLogged] = useState(false);
    const [interests, setInterests] = useState(["none", "no"]);

    const getInfo = (session) =>{
        userServices.getUser(session.idToken.payload['cognito:username']).then( data => {
            setInterests(data.data.interest);
        })
  }
    const InterestsToQuery = () =>{
        console.log(interests)
        let query = interests.join('').toLowerCase().replace(",", "OR")
        return query;
    }

    var CustomizedCategory = [
        {   
            value: InterestsToQuery(),
            name: 'For You'
        },
        {
            value: 'business',
            name: 'Business'
        },
        {
            value: 'entertainment',
            name: 'Entertainment'
        },
        {
            value: 'general',
            name: 'General'
        },
        {
            value: 'health',
            name: 'Health'
        },
        {
            value: 'science',
            name: 'Science'
        },
        {
            value: 'sports',
            name: 'Sports'
        },
        {
            value: 'technology',
            name: 'Technology'
        }
    ]
    var Country = [
        {
            value: 'us',
            name: 'Usa'
        },
        {
            value: 'in',
            name: 'India'
        },
        {
            value: 'au',
            name: 'Australia'
        },
        {
            value: 'ca',
            name: 'Canada'
        },
        {
            value: 'ch',
            name: 'China'
        }

    ]
    var Category = [
        {
            value: 'business',
            name: 'Business'
        },
        {
            value: 'entertainment',
            name: 'Entertainment'
        },
        {
            value: 'general',
            name: 'General'
        },
        {
            value: 'health',
            name: 'Health'
        },
        {
            value: 'science',
            name: 'Science'
        },
        {
            value: 'sports',
            name: 'Sports'
        },
        {
            value: 'technology',
            name: 'Technology'
        }
    ]
    useEffect(() => { 
        getSession().then((session) => {  
            setLogged(true); 
            getInfo(session);
        });
      }, []);
    return (
        <div className="filters">
            <span className="custom_dropdown">
                <select name="Category" id="Category"
                    value={props.selectedValueCategory}
                    onChange={props.handleChangeCategory}>
                    { logged ? CustomizedCategory.map(o =>
                            (<option key={o.value} value={o.value}>{o.name}</option>
                            )) : Category.map(o =>
                                (<option key={o.value} value={o.value}>{o.name}</option>
                                ))
                    }
                </select>
            </span>
            <span className="custom_dropdown">
                <select name="Country" id="Country"
                    value={props.selectedValueCountry}
                    onChange={props.handleChangeCountry}>
                    {
                        Country.map(o =>
                            (<option key={o.value} value={o.value}>{o.name}</option>
                            ))
                    }
                </select>
            </span>
        </div>
    )
}

const FilterModal = (props) => {
        return (
            <div>
                <Account>
                    <CFilterModal props = {props}/>
                </Account>
            </div>
        )
    };


export default FilterModal;
