import React, { Component, useEffect, useContext, useState } from "react";
import { Account, AccountContext } from "../cognito/Account";
import userServices from "../services/user.services";
import Interests from "../components/interest";
import "../css/user.css"

const CUser = () => {
    const { getSession } = useContext(AccountContext);
    const [id, setId] = useState();
    const [info, setInfo] = useState();
    const [interests, setInterests] = useState([]);
    const [availableInterests, setAvailableInterest] = useState([]);
    var removal = [];

    const getInfo = (session) => {
        userServices.getUser(session.idToken.payload['cognito:username']).then(data => {
            setInfo(data.data.email);
            setInterests(data.data.interest);
        })
    }

    const listofremoval = (e) => {
        if (removal.includes(e)) {
            removal.splice(removal.indexOf(e), 1)
        } else {
            removal.push(e);
        }
        console.log(removal)
    }

    const removeInterests = () => {
        setInterests(interests.filter(interest => !removal.includes(interest)));
    }

    const handleConfirm = async() => {
        removeInterests();
        window.alert("Are you sure?")

    }

    const handleSave = async() => {
        userServices.updateInterest(id, interests)
    }

    useEffect(() => {
        getSession().then((session) => {
            setId(session.idToken.payload['cognito:username']);
            getInfo(session)
            setAvailableInterest(Interests.getAvailableInterests(interests))
        });
    }, []);

    return ( <
        div className = "user-container" >
        <
        div className = "user-box" >
        <
        div className = "user-title" > User Info < /div> <
        div className = "user-info" >
        <
        div > < b > Email: < /b>{info}</div >
        <
        div className = "user-interest" >
        <
        b > Interests: < /b> {interests.map((item, index) => (<button onClick={ () => listofremoval(item, 0)} className="interest" key={index}>{item}</button > ))
} <
/div> <
div className = "user-interest" >
    <
    b > Do you want to remove these interests: < /b>  {removal}  <
    /div> <
    div className = "user-interest" >
    <
    b > Available Interests: < /b>{availableInterests.map((item, index) => (<button className="interest" key={index}>{item}</button > ))
} <
/div> <
div >
    <
    button onClick = {
        () => handleConfirm() } > Confirm < /button> <
    button onClick = {
        () => handleSave() } > Save < /button> <
    /div> <
    /div> <
    /div> <
    /div>
);
};

export default class User extends Component {
    render() {
        return ( <
            div >
            <
            Account >
            <
            CUser / >
            <
            /Account> <
            /div>
        )
    };

};
