import React, { Component } from 'react';
import NewsCard from '../component/newscard';



class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            news: [],
        };
    }

    componentDidMount() {
        const url = `https://newsapi.org/v2/everything?q=Apple&from=2021-08-24&sortBy=popularity&apiKey=82e8108c911440958306beda07b4d284`;

        fetch(url)
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                this.setState({
                    news: data.articles
                })
            })
            .catch((error) => console.log(error));
    }

    // take data from the api to render 
    renderItem() {
        return this.state.news.map((item) => (
            <NewsCard key={item.url} item={item} />
        ));
    }


    render() {
        return (
            <div className="news-container">
                {this.renderItem()}
            </div>
        );
    }
}

export default Home;
