import React, { Component, useEffect, useContext, useState } from "react";
import { Account, AccountContext } from "../cognito/Account";
import userServices from "../services/user.services";
import Interests from "../components/interest";
import "../css/user.css"

const CUser = () => {
    const { getSession } = useContext(AccountContext);
    const [info, setInfo] = useState();
    const [interests, setInterests] = useState([]);
    const [availableInterests, setAvailableIntersest] = useState([]);
    const removal = [];

    const getInfo = (session) => {
        userServices.getUser(session.idToken.payload['cognito:username']).then(data => {
            setInfo(data.data.email);
            setInterests(data.data.interest);
            setAvailableIntersest(Interests.getAvailableInterests(info))
        })
    }

    const listofremoval = (e) => {

        removal.push(e);
        console.log(removal)
    }

    const removeInterests = (e) => {
        setInterests(interests.filter(interest => !removal.includes(interest)));
    }

    useEffect(() => {
        getSession().then((session) => {
            getInfo(session);
        });
    }, []);

    return ( <
        div className = "user-container" >
        <
        div className = "user-box" >
        <
        div className = "user-title" > User Info < /div> <
        div className = "user-info" >
        <
        div > < b > Email: < /b>{info}</div >
        <
        div className = "user-interest" >
        <
        b > Interests: < /b> {interests.map((item, index) => (<input type="checkbox" onClick={ () => listofremoval(item, 0)} className="interest" key={index}>{item}</input > ))
} <
/div> <
div className = "user-interest" >
    <
    b > Available Interests: < /b>{availableInterests.map((item, index) => (<button className="interest" key={index}>{item}</button > ))
} <
/div> <
/div> <
/div> <
/div>
);
};

export default class User extends Component {
    render() {
        return ( <
            div >
            <
            Account >
            <
            CUser / >
            <
            /Account> <
            /div>
        )
    };

};
