import React, { Component } from "react";
import { validEmailRegex,validateForm,validNameRegex } from '../formvalidation/formhandlers.js';
import '../css/login_signup.css'
export default class SignUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstname:null,
            lastname:null,
            email: null,
            password: null,
            errors: {
                firstname:'',
                lastname:'',
                email: '',
                password: '',
          }
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange = (event) => {
        event.preventDefault();
        const { name, value } = event.target;
        let errors = this.state.errors;

        switch (name) {
            case 'firstname': 
                errors.firstname = 
                validNameRegex.test(value)
                    ? ''
                    : 'First name is not valid!';
                break;
            case 'lastname': 
                errors.lastname = 
                validNameRegex.test(value)
                    ? ''
                    : 'Last name is not valid!';
                break;
            case 'email': 
                errors.email = 
                validEmailRegex.test(value)
                    ? ''
                    : 'Email is not valid!';
                break;
            case 'password': 
                errors.password = 
                value.length < 8
                    ? 'Password must be 8 characters long!'
                    : '';
                break;
            default:
                break;
        }

        this.setState({errors, [name]: value});
      }

      handleSubmit = (event) => {
        event.preventDefault();
        if(validateForm(this.state.errors)) {
          console.info('Valid Form')
        }else{
          console.error('Invalid Form')
        }
      }
    render() {
        const {errors} = this.state;
        return (
            <div className = "page-container">
            <form onSubmit={this.handleSubmit} noValidate>
                <div className = "page-title">Sign Up</div>
                <div className="form-group">
                    <label htmlFor="firstname">First name</label>
                    <input type="text" className="form-control" name="firstname" placeholder="Enter first name" 
                    value={this.state.firstname}
                    onChange={this.handleChange} noValidate/>
                    {errors.firstname.length > 0 && <span className='error'>{errors.firstname}</span>}
                </div>

                <div className="form-group">
                    <label htmlFor="lastname">Last name</label>
                    <input type="text" className="form-control" name="lastname" placeholder="Enter last name" 
                    value={this.state.lastname}
                    onChange={this.handleChange} noValidate/>
                    {errors.lastname.length > 0 && <span className='error'>{errors.lastname}</span>}
                </div>

                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input type="email" className="form-control" name="email" placeholder="Enter email" 
                    value={this.state.email}                    
                    onChange={this.handleChange} noValidate/>
                    {errors.email.length > 0 && <span className='error'>{errors.email}</span>}
                </div>

                <div className={`form-group`}>
                    <label htmlFor="password">Password</label>
                    <input type="password" className="form-control" name="password" placeholder="Enter password"
                    value={this.state.password}                    
                    onChange={this.handleChange} noValidate/>
                    {errors.password.length > 0 && <span className='error'>{errors.password}</span>}
                </div>
                <div className='info'>
                    <small>Password must be eight characters in length.</small>
                </div>
                <div className='submit'>
                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                </div>
                <p className="forgot-password text-right">
                    Already registered <a href="/sign-in">sign in?</a>
                </p>
            </form>
            </div>

        );
    }
}
